// Kleines Inline-Script für die Admin-Seite
add_action('admin_enqueue_scripts', function($hook){
  if($hook !== 'toplevel_page_ai-patterns'){ return; }
  wp_add_inline_script('jquery', "(function(){var b=document.getElementById('ai_save');var t=document.getElementById('ai_html');var s=document.getElementById('ai_status');function n(m){s.textContent=m;} b&&b.addEventListener('click', async function(){var html=(t.value||'').trim(); if(!html||html.indexOf('<!-- wp:')===-1){ alert('Bitte gültiges Gutenberg-Block-HTML einfügen.'); return; } n('Speichere…'); try { var res = await fetch(window.AI_PATTERNS.endpoint,{method:'POST',headers:{'Content-Type':'application/json','X-WP-Nonce':window.AI_PATTERNS.nonce},body:JSON.stringify({title:'AI Pattern – Entwurf', content:html})}); if(!res.ok){ throw new Error('Fehler: '+res.status); } n('Gespeichert! Öffne im Editor den Block-Inserter → AI Patterns.'); } catch(err){ console.error(err); n('Fehler: '+err.message); } });})();");
});

// REST-Endpoint: /wp-json/ai-patterns/v1/create
add_action('rest_api_init', function(){
  register_rest_route('ai-patterns/v1','/create',[
    'methods'  => 'POST',
    'permission_callback' => function(){ return current_user_can('edit_posts'); },
    'args' => [
      'title'   => ['required'=>true,'type'=>'string'],
      'content' => ['required'=>true,'type'=>'string']
    ],
    'callback' => function( WP_REST_Request $req ){
      $title   = sanitize_text_field($req->get_param('title'));
      $content = $req->get_param('content');
      if (strpos($content, '<!-- wp:') === false) {
        return new WP_REST_Response(['ok'=>false,'error'=>'no_gutenberg_html'], 400);
      }
      if (!function_exists('register_block_pattern_category')) {
        require_once ABSPATH . 'wp-admin/includes/plugin.php';
      }
      register_block_pattern_category('ai-patterns', ['label' => 'AI Patterns']);
      $slug = 'ai-pattern-' . wp_generate_password(8,false,false);
      register_block_pattern($slug, [
        'title'       => $title,
        'content'     => $content,
        'categories'  => ['ai-patterns']
      ]);
      return new WP_REST_Response(['ok'=>true,'slug'=>$slug], 200);
    }
  ]);
});
